glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
  precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
  in vec4 v_color;
  #if USE_TEXTURE
  in vec2 v_uv0;
  uniform sampler2D texture;
  #endif
  #if USE_TARGET
  uniform sampler2D targetTex;
  #endif
  uniform ubo {
    vec4 targetXYWH;
    vec4 targetRect;
    vec4 selfXYWH;
    vec4 selfRect;
    vec4 spSlice;
    vec4 selfSlice;
    vec4 color;
    float ran;
    float thickness;
    float strength;
  };
  vec4 getColor(vec2 uv0) {
    vec4 o = vec4(1, 1, 1, 1);
    #if USE_TARGET
  vec4 targetTex_tmp = texture(targetTex, (targetXYWH.xy + (targetRect.zw - selfRect.zw) / 2.0 / targetRect.zw * targetXYWH.zw + uv0 * targetXYWH.zw * selfRect.zw / targetRect.zw - selfXYWH.xy) / selfXYWH.zw);
  #if CC_USE_ALPHA_ATLAS_targetTex
      targetTex_tmp.a *= texture(targetTex, (targetXYWH.xy + (targetRect.zw - selfRect.zw) / 2.0 / targetRect.zw * targetXYWH.zw + uv0 * targetXYWH.zw * selfRect.zw / targetRect.zw - selfXYWH.xy) / selfXYWH.zw + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (targetTex_tmp.rgb * targetTex_tmp.rgb);
    o.a *= targetTex_tmp.a;
  #else
    o *= targetTex_tmp;
  #endif
    #elif USE_TEXTURE
  vec4 texture_tmp = texture(texture, uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
    #endif
    return o;
  }
  void main () {
    vec4 o = getColor(v_uv0);
    vec2 realUV0 = (v_uv0 - selfXYWH.xy) / selfXYWH.zw;
    vec2 realUV = realUV0;
#if USE_SLICE
    realUV = ((realUV - 0.0) / (spSlice.xy - 0.0) * (selfSlice.xy - 0.0) + 0.0) * step(realUV, spSlice.xy)
      + ((realUV - spSlice.xy) / (spSlice.zw - spSlice.xy) * (selfSlice.zw - selfSlice.xy) + selfSlice.xy) * (step(spSlice.xy, realUV) - step(spSlice.zw, realUV))
      + ((realUV - spSlice.zw) / (1.0 - spSlice.zw) * (1.0 - selfSlice.zw) + selfSlice.zw) * step(spSlice.zw, realUV.xy)
    ;
#endif
    float ava = 0.0;
    const float d2 = 1.0;
    for (float dy = -d2; dy <= d2; dy++){
      for (float dx = -d2; dx <= d2; dx++){
        ava += getColor(vec2(realUV0.x + dx * thickness / selfRect.z, realUV0.y + dy * thickness / selfRect.w) * selfXYWH.zw + selfXYWH.xy).w;
      }
    }
    ava /= (d2 * 2.0 + 1.0) * (d2 * 2.0 + 1.0);
    float value = smoothstep(0.0, 0.5 / strength, ava) - smoothstep(1.0 - 0.5 / strength, 1.0, ava);
    vec4 _o = vec4(color.xyz, color.w * value);
    #if SHOW_TARGET
      o.xyz += _o.xyz * _o.w;
    #else
      o.xyz = _o.xyz;
      o.a *= _o.w;
    #endif
    o *= v_color;
    ALPHA_TEST(o);
    gl_FragColor = o;
  }

